import React, { useState, useEffect } from 'react';
import './CandidateCard.css';
import { getCandidatePhoto, getInitials, isImageCached, getCachedImage } from '../utils/photoMapping';

const CandidateCard = ({ candidate, isTop10 }) => {
  const { name, flat, totalCount, totalValue, votes } = candidate;

  // Get candidate photo and initials
  const photoUrl = getCandidatePhoto(name);
  const initials = getInitials(name);

  // State for image loading
  const [imageError, setImageError] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageLoading, setImageLoading] = useState(true);

  // Check if image is cached
  const cachedImage = photoUrl ? getCachedImage(photoUrl) : null;
  const isCached = photoUrl ? isImageCached(photoUrl) : false;

  // Determine card color based on position
  const cardBgColor = isTop10 ? "#e8f5e8" : "#ffe8e8";
  const borderColor = isTop10 ? "#2E8B57" : "#dc3545";

  // Handle image load events
  const handleImageLoad = () => {
    setImageLoaded(true);
    setImageLoading(false);
    setImageError(false);
  };

  const handleImageError = () => {
    setImageError(true);
    setImageLoading(false);
    setImageLoaded(false);
  };

  // Preload image if not cached
  useEffect(() => {
    if (photoUrl && !isCached && !imageError) {
      setImageLoading(true);

      const img = new Image();
      img.crossOrigin = 'anonymous';

      img.onload = handleImageLoad;
      img.onerror = handleImageError;

      // Add timeout for image loading
      const timeout = setTimeout(() => {
        if (!imageLoaded) {
          handleImageError();
        }
      }, 15000); // 15 second timeout

      img.src = photoUrl;

      return () => {
        clearTimeout(timeout);
        img.onload = null;
        img.onerror = null;
      };
    } else if (isCached) {
      // Image is already cached, show it immediately
      setImageLoaded(true);
      setImageLoading(false);
    }
  }, [photoUrl, isCached]);

  return (
    <div 
      className="candidate-card"
      style={{
        border: `3px solid ${borderColor}`,
        backgroundColor: cardBgColor
      }}
    >
      {/* Header */}
      <div 
        className="card-header"
        style={{ backgroundColor: borderColor }}
      >
        {name.toUpperCase()}
      </div>

      {/* Card Body */}
      <div className="card-body">
        <div className="card-content">
          {/* Profile Photo */}
          <div className="profile-photo">
            {photoUrl && !imageError && (imageLoaded || isCached) ? (
              <img 
                src={isCached ? cachedImage.src : photoUrl} 
                alt={name}
                onLoad={handleImageLoad}
                onError={handleImageError}
                className="candidate-photo"
                style={{
                  opacity: imageLoading ? 0.5 : 1,
                  transition: 'opacity 0.3s ease-in-out'
                }}
              />
            ) : imageLoading ? (
              <div className="photo-loading">
                <div className="loading-spinner"></div>
                <span>Loading...</span>
              </div>
            ) : (
              <div className="photo-fallback">
                {initials}
              </div>
            )}
          </div>

          {/* Vote Information */}
          <div className="vote-info">
            <div className="vote-stats">
              <div className="stat-item">
                <span className="stat-label">Total Count:</span>
                <span className="stat-value">{totalCount}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Total Value:</span>
                <span className="stat-value">{totalValue}</span>
              </div>
            </div>


          </div>
        </div>
      </div>
    </div>
  );
};

export default CandidateCard;
