import React, { useState } from 'react';
import './CandidateCard.css';
import { getCandidatePhoto, getInitials } from '../utils/photoMapping';

const CandidateCard = ({ candidate, isTop10 }) => {
  const { name, flat, totalCount, totalValue, votes, position } = candidate;

  // Get candidate photo and initials
  const photoUrl = getCandidatePhoto(name);
  const initials = getInitials(name);

  // State for image loading error
  const [imageError, setImageError] = useState(false);

  // Determine card color based on position
  const cardBgColor = isTop10 ? "#e8f5e8" : "#ffe8e8";
  const borderColor = isTop10 ? "#2E8B57" : "#dc3545";

  return (
    <div 
      className="candidate-card"
      style={{
        border: `3px solid ${borderColor}`,
        backgroundColor: cardBgColor
      }}
    >
      {/* Position Number */}
      <div className="position-number">
        {position || 'N/A'}
      </div>

      {/* Header */}
      <div 
        className="card-header"
        style={{ backgroundColor: borderColor }}
      >
        {name.toUpperCase()}
      </div>

      {/* Card Body */}
      <div className="card-body">
        <div className="card-content">
          {/* Profile Photo */}
          <div className="profile-photo">
            {photoUrl && !imageError ? (
              <img 
                src={photoUrl} 
                alt={name}
                onError={() => setImageError(true)}
                className="candidate-photo"
              />
            ) : (
              <div className="photo-fallback">
                {initials}
              </div>
            )}
          </div>

          {/* Vote Information */}
          <div className="vote-info">
            <div className="vote-stats">
              <div className="stat-item">
                <span className="stat-label">Total Count:</span>
                <span className="stat-value">{totalCount}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Total Value:</span>
                <span className="stat-value">{totalValue}</span>
              </div>
            </div>


          </div>
        </div>
      </div>
    </div>
  );
};

export default CandidateCard;
