import React from 'react';
import './CandidateCard.css';

const CandidateCard = ({ candidate, isTop10 }) => {
  const { name, flat, totalCount, totalValue, votes } = candidate;

  // Get initials from name
  const initials = name.split(' ').map(word => word[0]).join('').slice(0, 2);

  // Determine card color based on position
  const cardBgColor = isTop10 ? "#e8f5e8" : "#ffe8e8";
  const borderColor = isTop10 ? "#2E8B57" : "#dc3545";

  return (
    <div 
      className="candidate-card"
      style={{
        border: `3px solid ${borderColor}`,
        backgroundColor: cardBgColor
      }}
    >
      {/* Header */}
      <div 
        className="card-header"
        style={{ backgroundColor: borderColor }}
      >
        {name.toUpperCase()} - Flat {flat}
      </div>

      {/* Card Body */}
      <div className="card-body">
        <div className="card-content">
          {/* Profile Photo */}
          <div className="profile-photo">
            {initials}
          </div>

          {/* Vote Information */}
          <div className="vote-info">
            <div className="vote-stats">
              <div className="stat-item">
                <span className="stat-label">Total Count:</span>
                <span className="stat-value">{totalCount} (Flat {flat})</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Total Value:</span>
                <span className="stat-value">{totalValue}</span>
              </div>
            </div>

            {/* Vote Grid */}
            <div className="vote-grid">
              <div className="vote-box">
                <span className="vote-label">920:</span>
                <span className="vote-value">{votes["920"]}</span>
              </div>
              <div className="vote-box">
                <span className="vote-label">1005:</span>
                <span className="vote-value">{votes["1005"]}</span>
              </div>
              <div className="vote-box">
                <span className="vote-label">1165:</span>
                <span className="vote-value">{votes["1165"]}</span>
              </div>
              <div className="vote-box">
                <span className="vote-label">1285:</span>
                <span className="vote-value">{votes["1285"]}</span>
              </div>
              <div className="vote-box">
                <span className="vote-label">1670:</span>
                <span className="vote-value">{votes["1670"]}</span>
              </div>
              <div className="vote-box">
                <span className="vote-label">-</span>
                <span className="vote-value">-</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CandidateCard;
